	  public function hideOrder() {

        $order_id = $this->request->get['order_id'];

         $sql="UPDATE `". DB_PREFIX ."order` SET `hide` = !`hide`  WHERE `" . DB_PREFIX . "order`.`order_id` = '" . (int)$order_id . "'";


        $query = $this->db->query($sql);

        echo json_encode($query);

    }

